def generate_html_menu(grouped_items):
    html = """<!DOCTYPE html>
<html lang="ru">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>–ü–µ—Ä–µ–≤–µ–¥–µ–Ω–Ω–æ–µ –º–µ–Ω—é</title>
    <style>
        body {
            font-family: 'Arial', sans-serif;
