        }
        h1 {
            text-align: center;
            color: #333;
            border-bottom: 2px solid #ddd;
            padding-bottom: 10px;
        }
        h2 {
            color: #555;
            margin-top: 30px;
            border-bottom: 1px solid #eee;
            padding-bottom: 5px;
        }
        .menu-item {
            margin-bottom: 20px;
            padding: 15px;
            background-color: white;
            border-radius: 5px;
            box-shadow: 0 2px 5px rgba(0,0,0,0.1);
        }
        .menu-item h3 {
            margin-top: 0;
            color: #333;
        }
        .ingredients {
            font-size: 0.9em;
            color: #666;
            margin-top: 10px;
        }
        .allergens {
            margin-top: 10px;
            font-size: 0.8em;
        }
