"""

    # –î–æ–±–∞–≤–ª–µ–Ω–∏–µ –∫–∞—Ç–µ–≥–æ—Ä–∏–π –∏ –ø–æ–∑–∏—Ü–∏–π –º–µ–Ω—é
    for category, items in grouped_items.items():
        html += f"\n    <h2>{category}</h2>\n"

        for item in items:
            html += "    <div class=\"menu-item\">\n"
            html += f"        <h3>{item['item']}</h3>\n"

            if not item.get('is_drink', False) and item.get('ingredients'):
                ingredients_text = ", ".join(item['ingredients'])
                html += f"        <div class=\"ingredients\">–°–æ—Å—Ç–∞–≤: {ingredients_text}</div>\n"

                # –î–æ–±–∞–≤–ª–µ–Ω–∏–µ –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏–∏ –æ–± –∞–ª–ª–µ—Ä–≥–µ–Ω–∞—Ö
                html += "        <div class=\"allergens\">\n"

                milk_status = item.get('has_milk', 'unknown')
                if milk_status == 'yes':
                    html += "            <span class=\"allergen-icon\">ü•õ</span> –°–æ–¥–µ—Ä–∂–∏—Ç –º–æ–ª–æ–∫–æ\n"
                elif milk_status == 'no':
                    html += "            <span class=\"allergen-icon\">‚úÖ</span> –ë–µ–∑ –º–æ–ª–æ–∫–∞\n"

                gluten_status = item.get('has_gluten', 'unknown')
                if gluten_status == 'yes':
                    html += "            <span class=\"allergen-icon\">üåæ</span> –°–æ–¥–µ—Ä–∂–∏—Ç –≥–ª—é—Ç–µ–Ω\n"
                elif gluten_status == 'no':
                    html += "            <span class=\"allergen-icon\">‚úÖ</span> –ë–µ–∑ –≥–ª—é—Ç–µ–Ω–∞\n"

                html += "        </div>\n"

            html += "    </div>\n"

    html += "</body>\n</html>"

